// NavBar.jsx
import React, { useState } from 'react';
import axios from 'axios';
import './style.component.css';
import { useNavigate } from 'react-router-dom';
import { useEffect } from 'react';
import { Drawer } from 'antd';
const API_URL = process.env.REACT_APP_API_URL;
const jwt = localStorage.getItem("JwtToken");
const NavBar = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [navLinks, setNavLinks ] = useState([]);
  const [drawerVisible, setDrawerVisible] = useState(false);
  const navigate = useNavigate();
  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const Home = (e) => {
    navigate(`${e}`)
  }

  const login = () => {
    navigate('/login')
  }

  const Signout = () => {
    localStorage.removeItem("JwtToken");
    navigate("/login", { replace: true });
  };

  const showDrawer = () => {
    setDrawerVisible(true);
  };

  const onClose = () => {
    setDrawerVisible(false);
  };

const option = {
  headers: {
    "ngrok-skip-browser-warning": true,
    'Access-Control-Allow-Origin': '*',
  }
}


useEffect(()=>{
  Global();
},[]);


  const Global = async() => {
    try{
      const response = await axios.get(`${API_URL}/api/global?populate[navigation][populate]=*`);
      setNavLinks(response.data.data.attributes.navigation.Links);
      console.log(navLinks,'response');
    }catch(e){
      console.error(e);
    }
  }



  return (
    <nav className=" bg-white dark:bg-gray-900 fixed w-full z-20 top-0 start-0 border-b border-gray-200 dark:border-gray-600">
    <div className="max-w-screen-xl flex flex-wrap items-center justify-between mx-auto p-4">
      <a href='' className='flex items-center space-x-3 rtl:space-x-reverse'>
    <h1 className='text-white'> IHF by javeed </h1>
      {/* <img className='h-8 w-auto' src='https://res.cloudinary.com/dx78kzenz/image/upload/v1703314037/header-logo_faxbai.png'/> */}
      </a>
      <div class="flex md:order-2 space-x-3 md:space-x-0 rtl:space-x-reverse">
      <button type="button" class="text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-4 py-2 text-center dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800">Get started</button>
      <button data-collapse-toggle="navbar-sticky" type="button" class="inline-flex items-center p-2 w-10 h-10 justify-center text-sm text-gray-500 rounded-lg md:hidden hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-gray-200 dark:text-gray-400 dark:hover:bg-gray-700 dark:focus:ring-gray-600" aria-controls="navbar-sticky" aria-expanded="false">
        <span class="sr-only">Open main menu</span>
        <svg class="w-5 h-5" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 17 14">
            <path stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M1 1h15M1 7h15M1 13h15"/>
        </svg>
    </button>
  </div>
  <div class="items-center justify-between hidden w-full md:flex md:w-auto md:order-1" id="navbar-sticky">
  <ul class="flex flex-col p-4 md:p-0 mt-4 font-medium border border-gray-100 rounded-lg bg-gray-50 md:space-x-8 rtl:space-x-reverse md:flex-row md:mt-0 md:border-0 md:bg-white dark:bg-gray-800 md:dark:bg-gray-900 dark:border-gray-700">
      {navLinks.map((nav) => (
        <li key={nav.id} className="relative text-white">
          <a onClick={() => { navigate(`${nav.href}`) }}>
            {nav.label}
          </a>
        </li>
      ))}
    </ul>
  </div>


    </div>
  </nav>
  );
}

export default NavBar;
