import React from 'react'

const Checkout = () => {
    const [cart, setCart] = useState([]); // State to manage cart items

    const removeFromCart = (courseId) => {
      setCart(cart.filter(course => course.id !== courseId));
    };
  
    // Sample courses data
    const courses = [
      { id: 1, title: 'Course 1', price: 10 },
      { id: 2, title: 'Course 2', price: 20 },
      { id: 3, title: 'Course 3', price: 30 },
    ];
  
    // Function to add item to cart
    const addToCart = (course) => {
      setCart([...cart, course]);
    };
  
    // Calculate subtotal
    const subtotal = cart.reduce((total, course) => total + course.price, 0);
  
    return (
      <div className="flex justify-center mt-10">
        <div className="flex flex-col w-1/2 pr-4">
          <h2 className="text-lg font-bold mb-4">Your Courses</h2>
          <div className="space-y-2">
            {courses.map(course => (
              <div key={course.id} className="flex items-center justify-between bg-gray-100 p-2 rounded-md">
                <span>{course.title}</span>
                <span>${course.price}</span>
                <button onClick={() => addToCart(course)} className="bg-blue-500 text-white px-2 py-1 rounded-md">Add to Cart</button>
              </div>
            ))}
          </div>
        </div>
        <div className="flex flex-col w-1/2 pl-4">
          <h2 className="text-lg font-bold mb-4">Checkout</h2>
          <div className="space-y-2">
            {cart.map(course => (
              <div key={course.id} className="flex items-center justify-between bg-gray-100 p-2 rounded-md">
                <span>{course.title}</span>
                <span>${course.price}</span>
                <button onClick={() => removeFromCart(course.id)} className="bg-red-500 text-white px-2 py-1 rounded-md">Remove</button>
              </div>
            ))}
          </div>
          <div className="mt-4">
            <p>Subtotal: ${subtotal}</p>
            <p>Total: ${subtotal}</p>
            <button className="bg-green-500 text-white px-4 py-2 rounded-md mt-4">Checkout</button>
          </div>
        </div>
      </div>
    );
}

export default Checkout
