// NavBar.jsx
import React, { useState } from 'react';
import axios from 'axios';
import './style.component.css';
import { useNavigate } from 'react-router-dom';
import { useEffect } from 'react';
import { Drawer } from 'antd';
const API_URL = process.env.REACT_APP_API_URL;
const jwt = localStorage.getItem("JwtToken");
const NavBar = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [navLinks, setNavLinks ] = useState([]);
  const [drawerVisible, setDrawerVisible] = useState(false);
  const navigate = useNavigate();
  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const Home = (e) => {
    navigate(`${e}`)
  }

  const login = () => {
    navigate('/login')
  }

  const Signout = () => {
    localStorage.removeItem("JwtToken");
    navigate("/login", { replace: true });
  };

  const showDrawer = () => {
    setDrawerVisible(true);
  };

  const onClose = () => {
    setDrawerVisible(false);
  };

const option = {
  headers: {
    "ngrok-skip-browser-warning": true,
    'Access-Control-Allow-Origin': '*',
  }
}


useEffect(()=>{
  Global();
},[]);


  const Global = async() => {
    try{
      const response = await axios.get(`${API_URL}/api/global?populate[navigation][populate]=*`);
      setNavLinks(response.data.data.attributes.navigation.Links);
      console.log(navLinks,'response');
    }catch(e){
      console.error(e);
    }
  }



  return (
    <nav className="bg-white dark:bg-gray-900 fixed w-full z-20 top-0 start-0 border-b border-gray-200 dark:border-gray-600">
    <div className="max-w-screen-xl flex items-center justify-between mx-auto p-4">
      <a href="/" className="flex items-center space-x-3 rtl:space-x-reverse">
        <h1 className="text-black dark:text-white">IHF by Javeed</h1>
      </a>
      <div className="flex md:order-2 space-x-3 md:space-x-0 rtl:space-x-reverse">
        {jwt ? (
          <button className="btn" onClick={login}>
            Login
          </button>
        ) : (
          <button className="btn" onClick={Signout}>
            Logout
          </button>
        )}
        <button
          data-collapse-toggle="navbar-sticky"
          type="button"
          className="inline-flex items-center p-2 w-10 h-10 justify-center text-sm text-gray-500 rounded-lg md:hidden hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-gray-200 dark:text-gray-400 dark:hover:bg-gray-700 dark:focus:ring-gray-600"
          aria-controls="navbar-sticky"
          aria-expanded="false"
        >
          <span className="sr-only">Open main menu</span>
          <svg
            className="w-5 h-5"
            aria-hidden="true"
            xmlns="http://www.w3.org/2000/svg"
            fill="none"
            viewBox="0 0 17 14"
          >
            <path
              stroke="currentColor"
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d="M1 1h15M1 7h15M1 13h15"
            />
          </svg>
        </button>
      </div>
      <div className="items-center justify-between mqhidden w-full md:flex md:w-auto md:order-1" id="navbar-sticky">
        <ul className="flex flex-row p-4 md:p-0 mt-4 font-medium border border-gray-100 rounded-lg bg-gray-50 md:space-x-8 rtl:space-x-reverse md:flex-row md:mt-0 md:border-0 md:bg-white dark:bg-gray-800 md:dark:bg-gray-900 dark:border-gray-700">
          {navLinks.map((nav) => (
            <li key={nav.id} className="relative text-black dark:text-white">
              <a
                href={nav.href}
                className="block py-2 px-3 text-black dark:text-white rounded md:bg-transparent md:text-blue-700 md:p-0 md:dark:text-blue-500"
              >
                {nav.label}
              </a>
            </li>
          ))}
        </ul>
      </div>
    </div>
  </nav>
  
  );
}

export default NavBar;
