import axios from "axios";
import React, { useEffect, useState } from "react";
import { QueryClient, useQuery, useQueryClient } from "react-query";
import { useParams } from "react-router-dom";
import Footer from "./Footer";
import NavBar from "./NavBar";
import "./style.component.css";
const API_URL = process.env.REACT_APP_API_URL;

const Checkout = () => {
    const [price , setPrice]= useState(0);
    const queryClient = useQueryClient();
    const { id } = useParams();

    const getCart = async() =>{
        const response = await axios.get(`${API_URL}/api/carts/${id}?populate[courses][populate]=*`)
        return response.data.data.attributes.courses.data;
    }
    const { data:carts,isLoading,error} = useQuery('cartData', getCart);


    const removeCartItem =async(courseId)=>{
        const response = await axios.put(`${API_URL}/api/carts/${id}`,{
            data:{
                courses:{
                    disconnect:[courseId]
                }
            }
        } )
        queryClient.invalidateQueries('cartData');
    }
    const calculateTotalPrice = async () => {
        let totalPrice = 0; 
        carts && carts.length > 0 && carts.map(item => {
          totalPrice += Number(item.attributes.Price);
        });
        setPrice(totalPrice);
    };
    console.log(price,'price');

      useEffect(()=>{
        calculateTotalPrice();
      },[carts])

    console.log('cart checkout',carts )


    const handlePayment = async(e)=>{
      const response = await axios.get(`${API_URL}/api/razorpay`,option1);
  
      e.preventDefault();
       const amount = 1;
        var options = {
          key: `${response.data.data.attributes.keyId}`,
          key_secret:`${response.data.data.attributes.keySecret}`,
          amount: amount *100,
          currency:"INR",
          name:"MovieMads",
  
          config: {
            display: {
              blocks: {
                banks: {
                  name: 'All payment methods',
                  instruments: [
                    {
                      method: 'upi',
                    },
                    {
                      method: 'card'
                    },
                    {
                        method: 'wallet'
                    },
                    {
                        method: 'netbanking'
                    },
                  ],
                },
              },
              sequence: ['block.banks'],
              preferences: {
                show_default_blocks: false
              },
            },
          },
          handler:  async function (Paymentresponse){
            window.location.href = "/";
            const response = await axios.post( `${API_URL}/api/contests/${Paymentresponse.razorpay_payment_id}/${localStorage.getItem('formId')}/payment`,{},option1);
            // console.log(response,'payment response');
            // window.location.reload();
          },
        };
        var pay = new window.Razorpay(options);
        pay.open();
      }





    if (isLoading) return <h1 className="text-black bg-white">Fetching posts...</h1>;
    if (error) return <div>An error occurred: {error.message}</div>;
  return (
    <>
    <NavBar/>
    {/* {!carts ? ( */}
<div className="h-screen" >
        <div id="main-content" className="flex items-center max-h-screen justify-center mt-10  text-white">
      <div id="content-area" className="flex flex-wrap gap-10 ">
        <div id="left-area" className="w-2/3">
          <h1 className="main-title">Basket</h1>
          <div className="entry-content">
            <div className="cart-items">
              <table className="cart-items-table">
                <thead>
                  <tr className="cart-items-header">
                    <th><span>Product</span></th>
                    <th><span>Total</span></th>
                  </tr>
                </thead>
                <tbody>
                 {carts && carts.map((cart, index)=>(
                  <tr className="cart-item" key={index}>
                    <td className="flex  gap-8"><img className="h-[80px]" src={`${API_URL}${cart.attributes.CourseImage.data.attributes.url}`} alt="Full Cutting Package" />
                      <div className="cart-item-details">
                        <span>{cart.attributes.CourseName}</span>
                        <div className="cart-item-price">
                          <span>₹{cart.attributes.Price}</span>
                        </div>
                        <div className="cart-item-description">
                          <p>{cart.attributes.CheckoutLabel}</p>
                        </div>
                        <div className="cart-item-actions">
                          <button aria-label="Remove Full Cutting Package from basket" onClick={() => removeCartItem(cart.id)} >Remove item</button>
                        </div>
                      </div>
                    </td>
                    <td><span>₹{cart.attributes.Price}</span></td>
                  </tr>
                 ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
        <div className=" w-1/4">
          <div className="cart-totals flex flex-col justify-between h-full">
            <div className="basket-totals">
              <h2 className="basket-totals-heading">Basket totals</h2>
              <div className="basket-subtotal">
                <span>Subtotal</span>
                <span>&nbsp;&nbsp; ₹{price}</span>
              </div>
              <div className="basket-total">
                <span>Total</span>
                <span>&nbsp;&nbsp; ₹{price}</span>
              </div>
            </div>
            <button className="proceed-to-checkout mb-3 bg">
              <a href="https://thelondonhairdressingacademy.com/checkout/">Proceed to Checkout</a>
            </button>
          </div>
        </div>
      </div>
  </div>
  </div>
        
    {/* ): (
        <h1 className="h-screen text-white items-center justify-center text-center">Cart is empty</h1>
    )} */}
    
  <Footer/>
  </>
  );
};

export default Checkout;
