// NavBar.jsx
import React, { useState } from 'react';
import axios from 'axios';
import './style.component.css';
import { useNavigate } from 'react-router-dom';
import { useEffect } from 'react';
import { Drawer } from 'antd';
const API_URL = process.env.REACT_APP_API_URL;
const jwt = localStorage.getItem("JwtToken");
const NavBar = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [navLinks, setNavLinks ] = useState([]);
  const [drawerVisible, setDrawerVisible] = useState(false);
  const navigate = useNavigate();
  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const Home = (e) => {
    navigate(`${e}`)
  }

  const login = () => {
    navigate('/login')
  }

  const Signout = () => {
    localStorage.removeItem("JwtToken");
    navigate("/login", { replace: true });
  };

  const showDrawer = () => {
    setDrawerVisible(true);
  };

  const onClose = () => {
    setDrawerVisible(false);
  };

const option = {
  headers: {
    "ngrok-skip-browser-warning": true,
    'Access-Control-Allow-Origin': '*',
  }
}


useEffect(()=>{
  Global();
},[]);


  const Global = async() => {
    try{
      const response = await axios.get(`${API_URL}/api/global?populate[navigation][populate]=*`);
      setNavLinks(response.data.data.attributes.navigation.Links);
      console.log(navLinks,'response');
    }catch(e){
      console.error(e);
    }
  }



  return (
    <nav className=" bg-white dark:bg-gray-900 fixed w-full z-20 top-0 start-0 border-b border-gray-200 dark:border-gray-600">
    <div className="max-w-screen-xl flex flex-wrap items-center justify-between mx-auto p-4">
      
    <h1 className='text-white'> IHF by javeed </h1>
      {/* <img className='h-12 w-auto' src='https://res.cloudinary.com/dx78kzenz/image/upload/v1703314037/header-logo_faxbai.png'/> */}
    </div>
    <ul class="flex flex-col p-4 md:p-0 mt-4 font-medium border border-gray-100 rounded-lg bg-gray-50 md:space-x-8 rtl:space-x-reverse md:flex-row md:mt-0 md:border-0 md:bg-white dark:bg-gray-800 md:dark:bg-gray-900 dark:border-gray-700">
      {navLinks.map((nav) => (
        <li key={nav.id} className="relative text-white">
          <a onClick={() => { navigate(`${nav.href}`) }}>
            {nav.label}
          </a>
        </li>
      ))}
    </ul>
    <div className="mq450:hidden flex items-center justify-center gap-2">
      <div>
      </div>
      <div>
      {jwt?(
        <button className='btn' onClick={login}>Login</button>
      ):(
      <button className='btn' onClick={Signout}>Logout</button>
      )
    }
      </div>

    </div>

    {/* <div className="flex items-center justify-end md:hidden">
      <button onClick={showDrawer} className="text-gray-600 hover:text-gray-800 focus:outline-none focus:text-gray-800">
        <svg
          xmlns="http://www.w3.org/2000/svg"
          className="h-6 w-6"
          fill="none"
          viewBox="0 0 24 24"
          stroke="currentColor"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d={drawerVisible ? "M6 18L18 6M6 6l12 12" : "M4 6h16M4 12h16M4 18h16"}
          />
        </svg>
      </button>
    </div>

    <Drawer
      title="Menu"
      placement="right"
      closable={false}
      onClose={onClose}
      visible={drawerVisible}
      className="text-white md:hidden"
    >
      {navLinks.map((nav) => (
        <div key={nav.id} className="relative">
          <a onClick={() => { navigate(`${nav.href}`) }}>
            {nav.label}
          </a>
        </div>
      ))}
      <div>
        <button onClick={login}>Login</button>
      </div>
    </Drawer> */}
  </nav>
  );
}

export default NavBar;
